/*
 * * * * *
 * NOTES *
 * * * * *
 * Java executes code in order, from top to bottom
 *     From the first line, to the last line
 * 
 */

// CODE EXECUTION // 
package mikeDaneJavaTutorial;

public class Tutorial4PrintStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("   /|"); // First line that gets executed; prints to console
		System.out.println("  / |"); // Second line of code that gets executed
		System.out.println(" /  |"); // Third line/instruction
		System.out.println("/___|"); // Final line of code
	}
}